# <bitbar.desc>
#   Displays the Community Boating flag color and some weather information
#   from their station.
#   Community Boating: https://www.community-boating.org/
# </bitbar.desc>
# <bitbar.dependencies>python,beautifulsoup4,requests</bitbar.dependencies>

import re

import os
import requests
import time
from bs4 import BeautifulSoup

# Flag pictures, base64 encoded
GREEN = ""
YELLOW = ""
RED = ""
CLOSED = ""


def main():
    # Get the flag color code.
    response = requests.get(
        'https://portal2.community-boating.org/pls/apex/CBI_PROD.FLAG_JS'
    )
    content = response.content.decode('utf-8')
    flag_code = re.search(r'^var FLAG_COLOR = "(\w)";$', content).group(1)
    if flag_code == 'G':
        flag_image = GREEN
    elif flag_code == 'Y':
        flag_image = YELLOW
    elif flag_code == 'R':
        flag_image = RED
    elif flag_code == 'C':
        flag_image = CLOSED
    else:
        raise Exception("No valid flag code found")

    # Get detailed weather information.
    response = requests.get(
        'https://www.hobolink.com/p/0cdac4a6910cef5a8883deb005d73ae1'
    )
    soup = BeautifulSoup(response.content, 'html.parser')
    weather_table = soup.find(
        'tbody',
        id='hobolink-latest-conditions-form:smart-sensors-table-id_7162_data'
    )

    print("| image={}".format(flag_image))
    print('---')
    print(
        "Community Boating | " +
        "href=https://www.community-boating.org/about-us/weather-information/"
    )
    print('---')
    print(
        "Weather Information | " +
        "href=http://www.cesn.org/live/charles.php"
    )
    for row in weather_table.find_all('tr', class_='ui-widget-content'):
        print(
            row.text.replace('\n', '').replace('', '')
        )

    print('---')
    print("Happy sailing!")


if __name__ == '__main__':
    main()
